$(window).on('scroll', function() {
  if (window.document.body.id === 'top'){
    scrollHeight = $(document).height();
    scrollPosition = $(window).height() + $(window).scrollTop();
    if ( (scrollHeight - scrollPosition) / scrollHeight <= 0.05) {
          $('.jscroll').jscroll({
            contentSelector: '.jscroll',
            nextSelector: 'a.next',
            loadingHtml: '読み込み中'
          });
    }
  }
});
