function isCurrentPage() {
  if (document.getElementById('current') === true) {
    const page = document.getElementById('current');
    page.classList.remove('text-indigo-500', 'hover:bg-indigo-500', 'hover:text-white');
    page.classList.add('text-white', 'bg-indigo-500');
  }
}
window.addEventListener('load', isCurrentPage)
