        if (timeLeft < 0) {
          isRunning = false;
          start.textContent = 'Start';
          clearTimeout(timerId);
          timeLeft = 0;
          timeToCountDown = 0;
          updateTimer(timeLeft);
          document.getElementById('open').click();
          return;
        }
        updateTimer(timeLeft);
        countDown();
      }, 10);
    }


